public static String getValue(String url, String key){
    String k = key + "=";
    int begin = url.indexOf(k);
    if(begin == -1){
        return null;
    }
    begin = begin + k.length();
    int end = url.indexOf("&", begin);
    if(end == -1){
        return url.substring(begin);
    }else {
        return url.substring(begin, end);
    }
}
