}
function fetchRequest(url, method, params) {
    var headers = {
        "authorization": "XXXXXXXXX"
    }
    if( method.toLowerCase() == 'post' ){
        headers['Content-Type']= 'application/json'
    }
    return new Promise(function(resolve, reject) {
        fetch(url, {
            method: method,
            headers: headers,
            body: JSON.stringify(params)
        }).then(res => {
            return res.json()
        }).then(data => {
            resolve(data)
        }).catch(e => {
            console.log(e);
            // catch
        })
    })
}

