  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `max_id` bigint(20) NOT NULL DEFAULT 0 COMMENT '',
  `create_ts` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '',
  PRIMARY KEY (`id`),
