(function () {
    var Ticts=function Ticts() {
        this.ticts = {};
    };
    Ticts.prototype.createTicts=function(id, dealline) {
        var ticts=this;
        var dd = new Date(dealline)     // 
        var time = dd - new Date()      // 
        var _ticts=this.ticts[id] = {
            dealine: dealline
            , id: id
            , time: time
            , interval: setInterval(function () {
                var t = null;
                var d = null;
                var h = null;
                var m = null;
                var s = null;

                var now = new Date();
                t = _ticts.time / 1000; //js,
                var y = dd.getFullYear() - now.getFullYear()
                var M = dd.getMonth() - now.getMonth()
                var d = dd.getDate() - now.getDate()
                var h = dd.getHours() - now.getHours()
                if( M < 0 ){
                    y--;
                    M = M + 12
                }
                var flag = [1, 3, 5, 7, 8, 10, 12, 4, 6, 9, 11, 2];
                if( d < 0 ){
                    M--;
                    var index = flag.findIndex((temp) => {
                        return temp === now.getMonth() + 1
                    });
                    var monthLength;
                    if (index <= 6) {
                        monthLength = 31;
                    } else if (index > 6 && index <= 10) {
                        monthLength = 30;
                    } else {
                        monthLength = 28;
                    }
                    d = dd.getDate() + (monthLength - now.getDate());
                }
                if( h < 0 ){
                    d--;
                }
                t = t - y * 365 * 24 * 3600
                var total_day = Math.floor(t / (24 * 3600))
                t = t - 24 * 3600 * total_day
                h = Math.floor(t / 3600);
                t = t -h * 3600
                m = Math.floor(t / 60);
                t = t - m * 60
                s = Math.floor(t);
//,,DOM,
                document.getElementById(id).innerHTML = `${y}  ${M}  ${d}  ${h}  ${m}  ${s} `
                _ticts.time -= 1000;
                if (_ticts.time < 0)
ticts.deleteTicts(id);//,
            }, 1000)
        }
    };
    Ticts.prototype.deleteTicts = function(id) {
        clearInterval(this.ticts[id].interval);//,clearInterval
        delete this.ticts[id];//delete
    };
   //ticts,window,html(js)
    window.Ticts=new Ticts();
})();
```

