func Query(db *sql.DB) {
    rows, err := db.Query("select * from book")
    checkErr(err)
    defer rows.Close()

    books := make([]Book, 0)
    for rows.Next() {
        var b Book
        rows.Scan(&b.Id, &b.Name)
        books = append(books, b)
    }
