//  reflect  val  array 
func InArray(val interface{}, array interface{}) (index int) {
    index = -1
    switch reflect.TypeOf(array).Kind() {
        case reflect.Slice: {
            arr := reflect.ValueOf(array)
            for i := 0; i < arr.Len(); i++ {
                if reflect.DeepEqual(val, arr.Index(i).Interface()) {
                    index = i
                    return
                }
            }
        }
    }
    return
